import React from 'react';
import { Button, Form, FormGroup, Label, Input, Container } from 'reactstrap';
import {Link} from 'react-router-dom'
import '../App.css'
import Package from '../../package.json'
import FormHomePage from '../FormHomePage'
export default class LandingPage extends React.Component {
    constructor(props){
        super(props)
        this.state={
            name:'',
            title:'',
            content:''
        }
        this.onChangeTitle = this.onChangeTitle.bind(this)
        this.onChangeContent = this.onChangeContent.bind(this)
        this.submitRoziMantul = this.submitRoziMantul.bind(this)

    }
    onChangeTitle(event){
        this.setState({
            title:event.target.value
        })
        console.log(this.state.title)
    }
    onChangeContent(event){
        this.setState({
            content:event.target.value
        })
    }
    submitRoziMantul(){
        fetch("https://reduxblog.herokuapp.com/api/posts?key=RoziMantul", {
            method: 'POST', // *GET, POST, PUT, DELETE, etc.
            mode: 'cors', // no-cors, cors, *same-origin
            cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            credentials: 'same-origin', // include, *same-origin, omit
            headers: {
                'Content-Type': 'application/json',
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: 'follow', // manual, *follow, error
            referrer: 'no-referrer', // no-referrer, *client
            body: JSON.stringify({
                title: this.state.title,
                content: this.state.content
            }), // body data type must match "Content-Type" header
        })
        .then(response => response.json()) // parses JSON response into native JavaScript objects 
        .then(data => {
            console.log(data)
        })
    }
componentDidMount(){
    console.log(Package)
}
handleChangeName=(event)=>[
    this.setState({
        name: event.target.value
    })
]


    render(){


  return (
    <div  className='form-lp'>
        <Container>

      <br/>
      <Form  inline>
        <FormHomePage name={this.state.name} handleChangeName={this.handleChangeName} ></FormHomePage>
        <FormGroup className="mb-2 mr-sm-2 mb-sm-0">
          <Label for="title" className="mr-sm-2">Title</Label>
          <Input onChange={this.onChangeTitle} name="title"  placeholder="Title" />
        </FormGroup>
        <br/>
        <FormGroup className="mb-2 mr-sm-2 mb-sm-0">
          <Label for="content" className="mr-sm-2">Content</Label>
          <Input onChange={this.onChangeContent} name="content" placeholder="content" />
        </FormGroup>
        <Link to='#'>
        <Button className='warnaBiru' onClick={this.submitRoziMantul}>Submit</Button>
        </Link>
      </Form>   
      </Container>
       </div>
  );
}
}
