import React from 'react'
import { Link } from 'react-router-dom'

export default class Projects extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            fetched: []
        }
    }

    componentDidMount() {
        fetch('http://reduxblog.herokuapp.com/api/posts?key=uniquekey12389')
            .then(function (response) {
                return response.json();
            })
            .then( (myJson)=> {
                this.setState(state => ({
                    fetched: [...state.fetched, ...myJson]
                }))
            });
    }
    render() {

        return (
            <div>
                <Link to='/addproject'><button>Add Project</button></Link>
                <br />
                <ul>
                    <li>
                        {
                            this.state.fetched.map(fetch => {
                                return (
                                    <div>
                                    <p>Project Name: {fetch.title}</p>
                                    <p>Project Description: {fetch.content}</p>
                                    </div>
                        )
                            })
                        }
                    </li>
                </ul>
            </div>
        )
    }
}
