import React from 'react'
import { Link, Redirect } from 'react-router-dom'
import {Form, FormGroup, Label, Input, Button} from 'reactstrap'

export default class AddProjects extends React.Component {
    constructor(props){
        super(props)
        this.state ={
            title:'',
            description:'',
            url:'',
            redirect:false
        }
        this.handleDescription = this.handleDescription.bind(this)
        this.handleTitle = this.handleTitle.bind(this)
        this.handleUrl= this.handleUrl.bind(this)
        this.addProject = this.addProject.bind(this)
    }
    handleTitle(event){
        this.setState({
            title:event.target.value
        })
    }
    handleDescription(event){
        this.setState({
            description:event.target.value
        })
    }
    handleUrl(event){
        this.setState({
            url:event.target.value
        })
    }
    addProject(){
        fetch("http://reduxblog.herokuapp.com/api/posts?key=uniquekey12389", {
            method: 'POST', // *GET, POST, PUT, DELETE, etc.
            mode: 'cors', // no-cors, cors, *same-origin
            cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            credentials: 'same-origin', // include, *same-origin, omit
            headers: {
                'Content-Type': 'application/json',
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: 'follow', // manual, *follow, error
            referrer: 'no-referrer', // no-referrer, *client
            body: JSON.stringify({
                title: this.state.title,
                content: this.state.description,
                categories: this.state.url
            }), // body data type must match "Content-Type" header
        })
        .then(response => response.json()) // parses JSON response into native JavaScript objects 
        .then(data=>{
            this.setState({
                redirect:true
            })
        })
    }
    render() {
        if(this.state.redirect){
            return(
            <Redirect to='/projects'></Redirect>
            )
        }
        return (
            <div>
                <Link to='/projects'><button>Back To Projects</button></Link>
                <br />
                <div style={{width:'50%'}}>
                <Form style={{marginTop:'30px'}}>
                    <FormGroup className="mb-2 mr-sm-2 mb-sm-0">
                        <Label for="ProjectName" className="mr-sm-2">Project Name</Label>
                        <Input onChange={this.handleTitle} name="ProjectName" />
                    </FormGroup>
                    <FormGroup className="mb-2 mr-sm-2 mb-sm-0">
                        <Label for="ProjectDescription" className="mr-sm-2">Project Description</Label>
                        <Input onChange={this.handleDescription}  name="Project Description"  />
                    </FormGroup>
                    <FormGroup className="mb-2 mr-sm-2 mb-sm-0">
                        <Label for="ProjectDescription" className="mr-sm-2">Project Image Url</Label>
                        <Input onChange={this.handleUrl}  name="Project Description"  />
                    </FormGroup>
                    <br/>
                    <Link to={'#'}>
                        <Button onClick={this.addProject}>Submit</Button>
                    </Link>
                </Form>
                </div>
            </div>
        )
    }
}
