import React from 'react'
import { Link } from 'react-router-dom'
import {Row, Col} from 'reactstrap'

export default class Projects extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            fetched: []
        }
    }

    componentDidMount() {
        fetch('http://reduxblog.herokuapp.com/api/posts?key=uniquekey12389')
            .then(function (response) {
                return response.json();
            })
            .then((myJson) => {
                this.setState(state => ({
                    fetched: [...state.fetched, ...myJson]
                }))
            });
    }
    render() {
        //get()
        return (
            <div style={{ textAlign: 'center' }}>
                <Link to='/addproject'><button>Add Project</button></Link>
                <br />
                <ul style={{ listStyle: 'none' }}>
                <div style={{ borderBottom: '1px solid rgba(0,0,0,.2)', width: '50%', margin: '1em auto 0' }}></div>

                    {

                            this.state.fetched.map(fetch => {
                            return (
                                <li>

                                <div style={{ borderBottom: '1px solid rgba(0,0,0,.2)', width: '50%', margin: '1em auto 0' }}>
                                    <Row>
                                        <Col>
                                        <img style={{width:'100%'}} src={fetch.categories} alt=""/>

                                        </Col>
                                        <Col>
                                        <p style={{fontSize:"250%"}}><strong>{fetch.title}</strong></p>

                                        </Col>
                                    </Row>
                                </div>

                            </li>
                            )
                        })
                    }
                </ul>
            </div>
                )
            }
}
